definition.script:
pipeline {
    agent any

    stages {
        stage('Build-Airflow-Stack') {
            steps {

                sh '''
                        echo "Build Airflow Stack"
                        cd /opt/pic-sure-data-pipelines
                        docker-compose down
                        docker-compose build && docker-compose up -d
                        sleep 20

                 '''


            }
        }
    }
}
